INSERT INTO `x2_questions` VALUES (9925, 0, '赛车只有一个造型，水平向右，小雨想让赛车向左转弯，他需要用到下列哪个模块中的积木？（ ）', 1, 'peadmin', '', '&lt;p&gt;A:外观&amp;nbsp;B:控制&amp;nbsp;C:运动&amp;nbsp;D:侦测&lt;/p&gt;', 4, 'C', '', '', 1722085955, 1, '', 0, 0, 1, '', 0);
